-- creating tables users, posts, categories, comments with predefined constraints.
-- each id is unique and not empty, default entries are defined for creation time and view count.

create table users (
	user_id SERIAL PRIMARY KEY,
	username varchar(50) UNIQUE NOT NULL,
	email varchar(50) UNIQUE NOT NULL,
	creation_date date DEFAULT NOW(),
	is_active boolean
);


create table categories(
	category_id SERIAL UNIQUE PRIMARY KEY,
	name varchar(50) UNIQUE NOT NULL,
	creation_date date DEFAULT NOW()
);

create table comments(
	comment_id SERIAL UNIQUE PRIMARY KEY,
	post_id int NOT NULL,
	user_id int,
	comment text NOT NULL,
	creation_date date DEFAULT NOW(),
	is_confirmed boolean,
	CONSTRAINT user_id_comments_fk FOREIGN KEY (user_id) REFERENCES users(user_id)
);

create table posts(
	post_id int PRIMARY KEY,
	user_id int NOT NULL,
	category_id int NOT NULL,
	title varchar(50) NOT NULL,
	content text NOT NULL,
	view_count int DEFAULT 0,
	creation_date date DEFAULT NOW(),
	is_published boolean,
	CONSTRAINT user_id_fk FOREIGN KEY (user_id) REFERENCES users(user_id),
	CONSTRAINT cat_id_fk FOREIGN KEY (category_id) REFERENCES categories(category_id)
);

ALTER TABLE comments 
ADD CONSTRAINT post_id_fk FOREIGN KEY (post_id) REFERENCES posts(post_id);


